import React, { CSSProperties, ReactNode } from 'react';
import './OptionCell.styles.css';

interface HexagonalBlockProps {
	width: number;
	height: number;
	borderClass?: string;
	backgroundClass?: string;
	onClick?: () => void;
	children?: ReactNode;
}

const HexagonalBlock: React.FC<HexagonalBlockProps> = ({
														   width,
														   height,
														   borderClass,
														   backgroundClass,
														   onClick,
														   children,
													   }) => {
	const hexagonalStyle: CSSProperties = {
		width: `${width}px`,
		height: `${height}px`,
	};

	return (
		<div className="hex-block-container" style={hexagonalStyle} onClick={onClick}>
			<div className={`hex-block ${borderClass ? borderClass : ''}`}>
				<div className={`hexagon ${backgroundClass ? backgroundClass : ''}`}>
					{/* Лінії від бокової вершини */}
					<div className={`line left ${borderClass ? borderClass : ''}`}></div>
					<div className={`line right ${borderClass ? borderClass : ''}`}></div>
					{children}
				</div>
			</div>
		</div>
	);
};

export default HexagonalBlock;
