import {createAction, createSelector, createSlice} from '@reduxjs/toolkit';
import { RootState } from './store';
import gameConfig from '../gameConfig.json'

interface IQuestion {
	question: string
	reward: string
	options: {label: string, value: string}[]
	rightAnswers: string[]
}

export interface MainState {
	allQuestions: IQuestion[]
	currentQuestion: IQuestion | null
	currentIndex: number | null
	totalScore: number
}

const initialState: MainState = {
	allQuestions: gameConfig.questions,
	currentQuestion: null,
	currentIndex: null,
	totalScore: 0
};


export const mainSlice = createSlice({
	name: 'mainSlice',
	initialState,
	reducers: {
		// startGame: (state, action) => {
		// 	state.currentIndex = 0
		// 	state.currentQuestion = state.allQuestions[state.currentIndex]
		// }
	},
	extraReducers: (builder) => {
		builder.addCase('startGame', (state, action) => {
			state.currentIndex = 0
			state.currentQuestion = state.allQuestions[state.currentIndex]
		})
	}
});


const selectState = (state: RootState) => state
export const selectCurrentQuestion = createSelector(selectState, (state) => state.currentQuestion);

export default mainSlice.reducer;
