import React, {useEffect, useState} from "react";

import OptionCell from "../../components/OptionCell";
import './QuestionPage.styles.css'
import questions from '../../gameConfig.json'
import {useSelector} from "react-redux";
import {selectCurrentQuestion, selectCurrentIndex} from "../../redux/selectors";
import {highlightReward} from "../../utils";
import {useAppDispatch} from "../../utils/hooks";
import {nextQuestionAction} from "../../redux/actions";
import {useNavigate} from "react-router-dom";

export const QuestionPage: React.FC=() => {
	const dispatch=useAppDispatch()
	const navigate=useNavigate()
	const currentQuestion=useSelector(selectCurrentQuestion)
	const currentIndex=useSelector(selectCurrentIndex)

	const options=currentQuestion?.options;

	// Стан для відстеження вибраної відповіді
	const [selectedAnswer, setSelectedAnswer]=useState<string | null>(null);

	// Стан для відстеження статусу правильності відповіді
	const [isCorrectAnswer, setIsCorrectAnswer]=useState<boolean>(false);

	useEffect(() => {
		if (!selectedAnswer) {
			setIsCorrectAnswer(false);
			return;
		}

		// Перевірка, чи обрана відповідь правильна
		const isCorrect=options?.find((option) => option.label === selectedAnswer)?.isCorrect||false;
		setIsCorrectAnswer(isCorrect);

		// За 3 секунди скидаємо стан вибраної відповіді
		const timeoutId=setTimeout(() => {
			setSelectedAnswer(null);
			setIsCorrectAnswer(false);
		}, 3000);

		return () => clearTimeout(timeoutId); // Очищення таймеру при зміні стану
	}, [selectedAnswer, options]);


	// const handleSelectAnswer=(label: string) => {
	// 	if (selectedAnswer === '') {
	// 		setSelectedAnswer(label)
	// 	}
	// }
	// const handleCheckAnswer=() => {
	// 	if (currentQuestion?.rightAnswers.includes(selectedAnswer)) {
	// 		// dispatch(nextQuestionAction())
	// 	} else {
	// 		navigate('/final')
	// 	}
	// }
	// useEffect(() => {
	// 	if (currentIndex !== null&&currentIndex < 12) {
	// 		setSelectedAnswer('')
	// 	} else {
	// 		navigate('/final')
	// 	}
	// }, [currentIndex])
	//
	// useEffect(() => {
	// 	if (selectedAnswer) {
	// 		handleCheckAnswer()
	// 	}
	// }, [selectedAnswer])

	return (
		<div className={"QuestionPage-container"}>
			<div className={"Question-block"}>
				<h1 className={"Question-text"}>{currentQuestion?.question}</h1>
				<div className={"Options-container"}>
					{currentQuestion?.options.map((option: {
						label: string;
						value: string,
						isCorrect: boolean
					}, index: number) => {
						return (
							<OptionCell
								key={option.label}
								onClick={() => setSelectedAnswer(option.label)}
								className={`
            							${selectedAnswer === option.label ? 'selected' : ''}
            							${isCorrectAnswer&&option.label === selectedAnswer&&option.isCorrect ? 'correct' : ''}
            							${isCorrectAnswer&&option.label === selectedAnswer&& !option.isCorrect ? 'wrong' : ''}
          							`}
							>
								<span className={"Options-label"}>{option.label}</span>
								<span className={"Options-value"}>{option.value}</span>
							</OptionCell>
						)
					})}
				</div>
			</div>
			<div className={"Reward-block"}>
				{questions.questions.map(({reward}, index: number) => <OptionCell key={reward}
																				  className={`Reward`}
																				  rewardColor={`${highlightReward(currentIndex, index)}`}><span>${reward}</span></OptionCell>).reverse()}
			</div>
		</div>
	)
}
