import React, {useEffect, useState} from "react";

import OptionCell from "../../components/OptionCell";
import './QuestionPage.styles.css'
import questions from '../../gameConfig.json'
import {useSelector} from "react-redux";
import {selectCurrentQuestion, selectCurrentIndex, selectNumberOfAnswers} from "../../redux/selectors";
import {highlightReward} from "../../utils";
import {useAppDispatch} from "../../utils/hooks";
import {nextQuestionAction} from "../../redux/actions";
import {useNavigate} from "react-router-dom";


export const QuestionPage: React.FC=() => {
	const dispatch=useAppDispatch()
	const navigate=useNavigate()
	const currentQuestion=useSelector(selectCurrentQuestion)
	const currentIndex=useSelector(selectCurrentIndex)
	const numberOfCorrectAnswers = useSelector(selectNumberOfAnswers)
	const options=currentQuestion?.options || [];
	const [selectedAnswers, setSelectedAnswers] = useState<string[]>([]);

	// Стан для відстеження статусу правильності відповіді
	const [isCorrectAnswers, setIsCorrectAnswers] = useState<boolean | null>(null);

	// Стан для відстеження часу
	const [selectionTime, setSelectionTime] = useState<number | null>(null);

	// Обробник вибору відповіді
	const handleSelectAnswer = (selectedOption: string) => {
		// Якщо вже обрано максимальну кількість відповідей або пройшов час, не змінюємо вибір
		if (selectedAnswers.length >= numberOfCorrectAnswers || selectionTime !== null) {
			return;
		}

		setSelectedAnswers((prevSelected) => [...prevSelected, selectedOption]);
		setSelectionTime(Date.now());
	};

	useEffect(() => {
		if (selectedAnswers.length === 0) {
			setIsCorrectAnswers([]);
			return;
		}

		// Перевірка правильності через 3 секунди
		const timeoutId = setTimeout(() => {
			// Перевірка, чи обрані відповіді правильні
			const correctAnswers = options
				.filter((option) => selectedAnswers.includes(option.label))
				.map((option) => option.isCorrect);

			// Перевірка, чи кількість правильних відповідей співпадає з numberOfCorrectAnswers
			const isCorrect = correctAnswers.length === numberOfCorrectAnswers;

			setIsCorrectAnswers(isCorrect);
		}, 3000);

		return () => clearTimeout(timeoutId); // Очищення таймеру при зміні стану
	}, [selectedAnswers, options, numberOfCorrectAnswers]);

	// const [selectedAnswer, setSelectedAnswer] = useState<string | null>(null);
	//
	// // Стан для відстеження статусу правильності відповіді
	// const [isCorrectAnswer, setIsCorrectAnswer] = useState<boolean | null>(null);
	//
	// // Стан для відстеження часу
	// const [selectionTime, setSelectionTime] = useState<number | null>(null);
	//
	// // Обробник вибору відповіді
	// const handleSelectAnswer = (selectedOption: string) => {
	// 	// Якщо вже обрано відповідь або пройшов час, не змінюємо вибір
	// 	if (selectedAnswer !== null || selectionTime !== null) {
	// 		return;
	// 	}
	//
	// 	setSelectedAnswer(selectedOption);
	// 	setSelectionTime(Date.now());
	//
	// 	// Знаходимо, чи є обрана відповідь правильною
	// 	const isCorrect = options.find((option) => option.label === selectedOption)?.isCorrect || false;
	//
	// 	// Перевірка правильності через 3 секунди
	// 	setTimeout(() => {
	// 		setIsCorrectAnswer(isCorrect);
	// 	}, 3000);
	// };


	// const handleSelectAnswer=(label: string) => {
	// 	if (selectedAnswer === '') {
	// 		setSelectedAnswer(label)
	// 	}
	// }
	// const handleCheckAnswer=() => {
	// 	if (currentQuestion?.rightAnswers.includes(selectedAnswer)) {
	// 		// dispatch(nextQuestionAction())
	// 	} else {
	// 		navigate('/final')
	// 	}
	// }
	// useEffect(() => {
	// 	if (currentIndex !== null&&currentIndex < 12) {
	// 		setSelectedAnswer('')
	// 	} else {
	// 		navigate('/final')
	// 	}
	// }, [currentIndex])
	//
	// useEffect(() => {
	// 	if (selectedAnswer) {
	// 		handleCheckAnswer()
	// 	}
	// }, [selectedAnswer])

	return (
		<div className={"QuestionPage-container"}>
			<div className={"Question-block"}>
				<h1 className={"Question-text"}>{currentQuestion?.question}</h1>
				<div className={"Options-container"}>
					{currentQuestion?.options.map((option: {
						label: string;
						value: string,
						isCorrect: boolean
					}, index: number) => {
						return (
							<OptionCell
								key={option.label}
								onClick={() => handleSelectAnswer(option.label)}
								className={`${selectedAnswers.includes(option.label) ? 'selected' : ''}
            						${isCorrectAnswers.length > 0 &&
									selectedAnswers.includes(option.label) &&
									isCorrectAnswers[0]
										? 'correct'
										: ''}
           							${isCorrectAnswers.length > 0 &&
									selectedAnswers.includes(option.label) &&
									!isCorrectAnswers[0]
										? 'wrong'
										: ''}
										`}
							>
								<span className={"Options-label"}>{option.label}</span>
								<span className={"Options-value"}>{option.value}</span>
							</OptionCell>
						)
					})}
				</div>
			</div>
			<div className={"Reward-block"}>
				{questions.questions.map(({reward}, index: number) => {
					return (
						<OptionCell
							key={reward}
							className={`Reward`}
							rewardColor={`${highlightReward(currentIndex, index)}`}
						>
							<span>${reward}</span>
						</OptionCell>
					)
				}).reverse()}
			</div>
		</div>
	)
}
