import React, { CSSProperties, ReactNode } from 'react';
import './styles.css';

interface HexagonalBlockProps {
	blockClass?: string;
	borderClass?: string;
	backgroundClass?: string;
	width: number;
	height: number;
	onClick?: () => void;
	children?: ReactNode;
}

const HexagonalBlock: React.FC<HexagonalBlockProps> = ({
														   blockClass,
														   borderClass,
														   backgroundClass,
														   width,
														   height,
														   onClick,
														   children,
													   }) => {
	const hexagonalStyle: CSSProperties = {
		width: `${width}px`,
		height: `${height}px`,
	};

	const containerStyle: CSSProperties = {
		borderColor: borderClass,
		backgroundColor: backgroundClass,
	};

	return (
		<div className={'hex-block-container'} style={hexagonalStyle} onClick={onClick}>
			<div
				className={`${blockClass ? blockClass + '-before' : 'before'}${
					borderClass ? '-' + borderClass : ''
				}`}
			></div>
			<div
				className={`hex-block-content ${blockClass ? blockClass : ''}${
					borderClass ? '-' + borderClass : ''
				}`}
				style={containerStyle}
			>
				<div className="hex-block-inner-content">{children}</div>
			</div>
			<div
				className={`${blockClass ? blockClass + '-before' : 'before'}${
					borderClass ? '-' + borderClass : ''
				}`}
			></div>
		</div>
	);
};

export default HexagonalBlock;
