import React, {useEffect, useState} from "react";
import {useLinkClickHandler, useHref, redirect, useResolvedPath, useNavigate} from 'react-router-dom'
import CommonButton from "../../components/Button";
import {HandLogo} from "../../assets/HandLogo";
import './StartPage.styles.css'


export const StartPage: React.FC=() => {
	const navigate = useNavigate()
	// const hrefFunc = useHref('/game')
	// const path = useResolvedPath('/game')
	const [startGame, setStartGame] = useState(false)
	const handleStartGame = () => {
		// navigate('/game', {replace: true})
		setStartGame(true)
	}
	useEffect(() => {
		navigate('/game')
	}, [startGame])
	return (
			<div className="StartPage-background">
				<div className="StartPage-splitBackground">
						<div className="Hand-container"><HandLogo /></div>
						<div className="Greetings-container">
							<h1 className="Greetings-text">Who wants to be a millionaire?</h1>
							<CommonButton title='Start'  onClick={handleStartGame} path={'/game'}/>
						</div>
				</div>
			</div>
	)

}
