import React from 'react';
import './OptionCell.styles.css';

interface IOptionCell {
	children: React.ReactNode;
	className?: any;
	rewardColor?: string;
	onClick?: () => void;
	answerColor?: string;
	style?: {
		selected?: boolean;
		correct?: boolean;
		wrong?: boolean;
		// Додаткові стилі можуть бути додані тут
	};
}

export const OptionCell: React.FC<IOptionCell> = ({
											   children,
											   className = '',
											   onClick,
											   rewardColor,
	style
										   }) => {

	const optionCellClassName = [
		style?.selected ? 'selected' : '',
		style?.correct ? 'correct' : '',
		style?.wrong ? 'wrong' : '',
	].join(' ');
	const containerClass = `Container ${className}${rewardColor ? '-' + rewardColor : ''}`;
	const beforeClass = `${className ? className + '-before' : 'before'}${rewardColor ? '-' + rewardColor : ''}`;

	return (
		<div className="Main-container" onClick={onClick}>
			<div className={beforeClass}></div>
			<div className={containerClass} style={{zIndex:5}}>{children}</div>
			<div className={beforeClass}></div>
		</div>
	);
};
