import React, { CSSProperties, ReactNode }  from 'react';
import './OptionCell.styles.css';
interface HexagonalBlockProps {
	className?: string;
	borderColor?: string;
	backgroundColor?: string;
	width: number;
	height: number;
	onClick?: () => void;
	children?: ReactNode;
}
const HexagonalBlock: React.FC<HexagonalBlockProps> = ({ className, borderColor, backgroundColor, width, height, onClick, children }) => {
	const hexagonalStyle: CSSProperties = {
		width: `${width}px`,
		height: `${height}px`
	};

	const containerStyle: CSSProperties  = {
		borderColor: borderColor,
		backgroundColor: backgroundColor
	};

	return (
		<div className={'Main-container'} style={hexagonalStyle} onClick={onClick}>
			<div
				className={`${className ? className + '-before' : 'before'}${
					borderColor ? '-' + borderColor : ''
				}`}
			></div>
			<div
				className={`Container ${className ? className : ''}${borderColor ? '-' + borderColor : ''}`}
				style={containerStyle}
			>
				<div className="Content">{children}</div>
			</div>
			<div
				className={`${className ? className + '-before' : 'before'}${
					borderColor ? '-' + borderColor : ''
				}`}
			></div>
		</div>
	);
};

export default HexagonalBlock;
